module.exports = function uri(args){
	ret = "mongodb://" + (args.auth ? args.username + ":" + args.password + "@" : '') + args.ip + ":" + args.port + "/" + args.dbName + (args.auth ? "?authMechanism="+(args.authMechanism || 'DEFAULT')+"&authSource="+args.authDB+"" : '')
	return ret
}